body.script:
    // ----- Admin link management -----
    const defaultLinks = {
      recap: "#", pilotInstructions: "#", pilotForm: "#", promptKit: "#",
      roiCalc: "#", levelsAI: "#", modelGuide: "#", ethicsGuide: "#",
      deepResearch: "#", officeHours: "#", helpEmail: "mailto:help@example.com", siteUrl: "#"
    };

    function loadLinks() {
      try {
        const saved = JSON.parse(localStorage.getItem("execai_links") || "null");
        return saved && typeof saved === "object" ? {...defaultLinks, ...saved} : {...defaultLinks};
      } catch (e) { return {...defaultLinks}; }
    }

    function applyLinks(map) {
      document.querySelectorAll("[data-link-key]").forEach(a => {
        const key = a.getAttribute("data-link-key");
        if (map[key]) a.setAttribute("href", map[key]);
      });
    }

    function populateAdmin(map) {
      document.querySelectorAll("[data-admin]").forEach(inp => {
        const key = inp.getAttribute("data-admin");
        inp.value = map[key] || "";
      });
    }

    const links = loadLinks();
    applyLinks(links);

    // Toggle admin panel
    const panel = document.getElementById("adminPanel");
    const gear = document.getElementById("gear");
    gear.addEventListener("click", () => panel.style.display = panel.style.display === "block" ? "none" : "block");
    document.addEventListener("keydown", e => { if ((e.key === "e" || e.key === "E") && !e.metaKey && !e.ctrlKey) gear.click(); });

    populateAdmin(links);

    document.getElementById("saveAdmin").addEventListener("click", () => {
      const newMap = {};
      document.querySelectorAll("[data-admin]").forEach(inp => newMap[inp.getAttribute("data-admin")] = inp.value.trim());
      localStorage.setItem("execai_links", JSON.stringify(newMap));
      applyLinks(newMap);
      panel.style.display = "none";
      alert("Links saved for this browser.");
    });
    document.getElementById("resetAdmin").addEventListener("click", () => {
      localStorage.removeItem("execai_links");
      applyLinks(defaultLinks);
      populateAdmin(defaultLinks);
      alert("Links reset to placeholders.");
    });

    // ----- ROI calculator -----
    const teamSizeEl = document.getElementById("teamSize");
    const hoursPerEl = document.getElementById("hoursPerPerson");
    const rateEl = document.getElementById("loadedRate");
    const costEl = document.getElementById("toolCost");

    const fmt = n => isFinite(n) ? n.toLocaleString(undefined, {maximumFractionDigits: 2}) : "—";

    function calc() {
      const team = Math.max(0, Number(teamSizeEl.value));
      const hrs = Math.max(0, Number(hoursPerEl.value));
      const rate = Math.max(0, Number(rateEl.value));
      const cost = Math.max(0, Number(costEl.value));

      const weeklyHours = team * hrs;
      const weeklySavings = weeklyHours * rate;
      const annualHours = weeklyHours * 52;
      const annualSavings = weeklySavings * 52;
      const annualCost = team * cost * 12;
      const roiMultiple = annualCost > 0 ? annualSavings / annualCost : 0;

      document.getElementById("weeklyHours").textContent = fmt(weeklyHours) + " hrs";
      document.getElementById("weeklySavings").textContent = "$" + fmt(weeklySavings);
      document.getElementById("annualHours").textContent = fmt(annualHours) + " hrs";
      document.getElementById("annualSavings").textContent = "$" + fmt(annualSavings);
      document.getElementById("annualCost").textContent = "$" + fmt(annualCost);
      document.getElementById("roiMultiple").textContent = roiMultiple ? fmt(roiMultiple) + "×" : "—";

      // Persist inputs
      localStorage.setItem("execai_roi", JSON.stringify({team, hrs, rate, cost}));
    }

    [teamSizeEl, hoursPerEl, rateEl, costEl].forEach(el => el.addEventListener("input", calc));

    // Load saved ROI inputs
    try {
      const raw = JSON.parse(localStorage.getItem("execai_roi") || "null");
      if (raw) {
        teamSizeEl.value = raw.team ?? teamSizeEl.value;
        hoursPerEl.value = raw.hrs ?? hoursPerEl.value;
        rateEl.value = raw.rate ?? rateEl.value;
        costEl.value = raw.cost ?? costEl.value;
      }
    } catch (e) { /* noop */ }
    calc();

    document.getElementById("copyRoi").addEventListener("click", () => {
      const text = `ROI Summary\nTeam: ${teamSizeEl.value}\nHours saved per person per week: ${hoursPerEl.value}\nLoaded rate: ${rateEl.value}\nTool cost: ${costEl.value}/user/mo\nWeekly hours: ${document.getElementById("weeklyHours").textContent}\nWeekly savings: ${document.getElementById("weeklySavings").textContent}\nAnnual hours: ${document.getElementById("annualHours").textContent}\nAnnual savings: ${document.getElementById("annualSavings").textContent}\nAnnual tool cost: ${document.getElementById("annualCost").textContent}\nROI multiple: ${document.getElementById("roiMultiple").textContent}`;
      navigator.clipboard.writeText(text);
    });
    document.getElementById("resetRoi").addEventListener("click", () => {
      localStorage.removeItem("execai_roi");
      teamSizeEl.value = 10; hoursPerEl.value = 5; rateEl.value = 60; costEl.value = 30;
      calc();
    });

    // ----- Prompt kit copy buttons -----
    document.querySelectorAll(".copy").forEach(btn => {
      btn.addEventListener("click", () => {
        const sel = btn.getAttribute("data-copy");
        const el = document.querySelector(sel);
        if (!el) return;
        navigator.clipboard.writeText(el.textContent.trim());
        btn.classList.add("done");
        btn.textContent = "Copied";
        setTimeout(() => { btn.classList.remove("done"); btn.textContent = "Copy"; }, 1200);
      });
    });

    // ----- Levels of AI interactive panel -----
    const levels = {
      1: `
Level 1: Personal productivity


Email drafting and tone changes; meeting notes and summaries.

Research synthesis with citations; brainstorming and first drafts.

Prompts: “Rewrite for brevity and clarity.” “Summarize with next actions.”


Exit when: you save 3–5 hours a week consistently.`,
      2: `
Level 2: Team templates


Shared prompt packs for recurring work; role‑based checklists.

Standardized briefs, proposals, reports, and QA passes.

Prompts: “Use the team template v3 and deliver the draft with a QA list.”


Exit when: a teammate can get 80% quality without you.`,
      3: `
Level 3: Workflow automation


Link tools and reduce manual steps; approvals stay human.

Examples: intake to draft to review; research to summary to deck.

Prompts: “Produce JSON outputs for system X; include validation rules.”


Exit when: cycle time drops by 30% on a core process.`,
      4: `
Level 4: Data + agents


Use your data; specialize assistants by job family.

Add retrieval for policies, playbooks, and client facts.

Prompts: “Cite from our corpus; show source snippets in‑line.”


Exit when: assistants pass a shadow‑to‑live handoff.`,
      5: `
Level 5: AI products


External‑facing tools; measurable revenue or impact.

Telemetry and feedback loops; governance by design.

Prompts: “Propose v1 with scope, risks, and analytics to track.”


Exit when: the product meets a clear KPI and pays for itself.`
    };
    document.querySelectorAll(".step").forEach(el => el.addEventListener("click", () => {
      document.querySelectorAll(".step").forEach(s => s.classList.remove("active"));
      el.classList.add("active");
      const n = el.getAttribute("data-step");
      document.getElementById("levelContent").innerHTML = levels[n] || "";
    }));

    // ----- Pilot logger -----
    const fields = ["pilotName", "baseline", "approach", "results"];
    function savePilot() {
      const data = Object.fromEntries(fields.map(id => [id, document.getElementById(id).value]));
      localStorage.setItem("execai_pilot", JSON.stringify(data));
    }
    function loadPilot() {
      try {
        const data = JSON.parse(localStorage.getItem("execai_pilot") || "null");
        if (!data) return;
        fields.forEach(id => { if (data[id]) document.getElementById(id).value = data[id]; });
      } catch (e) { /* noop */ }
    }
    fields.forEach(id => document.getElementById(id).addEventListener("input", savePilot));
    loadPilot();

    function pilotSummary() {
      const v = Object.fromEntries(fields.map(id => [id, document.getElementById(id).value.trim()]));
      return `Pilot Summary\nProcess: ${v.pilotName || "—"}\nBaseline: ${v.baseline || "—"}\nApproach: ${v.approach || "—"}\nEarly results: ${v.results || "—"}`;
    }
    document.getElementById("copyPilot").addEventListener("click", () => navigator.clipboard.writeText(pilotSummary()));
    const copyPilotSummaryBtn = document.getElementById("copyPilotSummaryBtn");
    if (copyPilotSummaryBtn) copyPilotSummaryBtn.addEventListener("click", () => navigator.clipboard.writeText(pilotSummary()));
